$curDirectory = str_replace('\\', '/', substr(__DIR__, strlen($_SERVER['DOCUMENT_ROOT'])).'/');
if (isset($_POST['settings'])) {
	$base = $_POST['base'];
	if (empty($base)) {
		$base = $curDirectory;
	}

	$settings = $_POST['settings'];

	if (isset($_POST['delete'])) {
		$delete = $_POST['delete'];
	} else {
		$delete = 'false';
	}

	if (isset($_POST['deleteUpdate'])) {
		$deleteUpdate = $_POST['deleteUpdate'];
	} else {
		$deleteUpdate = 'false';
	}

	if (empty($settings)) {
		header('Location: urls_install.php?error=1&base='.urlencode($base).'&delete='.urlencode($delete).'&deleteUpdate='.urlencode($deleteUpdate));
		exit();
	}
	

	if ($htaccess = fopen('.htaccess', 'a')) {
		fwrite($htaccess, "# --URLS BEGIN--\n# The following lines between \"--URLS BEGIN--\" and \"--URLS END--\" are\n# automatically generated by the URLS framework. These lines should\n# not be edited as it may result in unwanted behavior of the site. Any\n# edits made may be overwritten automatically.\nOptions +FollowSymLinks\nRewriteEngine On\nRewriteBase ".$base."\nRewriteRule ^".preg_quote($settings)."$ - [L]\n# --URLS ADD_COND BEGIN--\n# --URLS ADD_COND END--\nRewriteCond %{REQUEST_FILENAME} !-d\nRewriteCond %{REQUEST_FILENAME} !-f [OR]\nRewriteCond %{REQUEST_FILENAME} \.php$\nRewriteRule . ".$base.$settings." [L]\nRewriteRule ^$ ".$base.$settings." [L]\n# --URLS END--\n");
		fclose($htaccess);
	} else {
		die('Error creating ".htaccess" file.');
	}

	if (!is_dir('urls')) {
		$dir = 'urls';
	} else {
		$dir = 'urls_'.uniqid();
	}

	if (!mkdir($dir)) {
		die('Error creating directory: '.$dir);
	}

	// Download the update files
	$updateFilePath = $dir.'/UrlsUpdate.zip';
	file_put_contents($updateFilePath, file_get_contents('https://raw.githubusercontent.com/urls-framework/URLS/2.0.0/update_files/UrlsUpdate.zip?'.mt_rand()));

	// Extract the zip folder
	$zip = new ZipArchive;
	$res = $zip->open($updateFilePath, PATHINFO_DIRNAME);
	if ($res === true) {
		$zip->extractTo($dir);
		$zip->close();
	} else {
		die('Error unpacking files.');
	}

	// Delete the update files
	if ($deleteUpdate === 'true') {
		unlink($updateFilePath);
	}

	$settingsFile = fopen($settings, 'w');
	fwrite($settingsFile, "<?php\n/*\nURLS framework url config file.\n\nAdd your paths here:\nex. \$urls->path('blog/', 'blog-home.php', true);\n*/\ninclude '".$dir."/Urls.php';\nUrls::\$base = '".$base."';\n\n\$urls = new Urls;\n\n\n\$urls->exe();\n\n?>\n");
	fclose($settingsFile);
	echo 'done';
	if ($delete == 'true') {
		if (!unlink('urls_install.php')) {
			exit('Error deleting install file.');
		}
	}
	exit();

}
?>
